import React, { Component } from 'react';
import { connect } from 'react-redux';


class TablePractice extends Component {
  render() {
    const colHeaders = this.props.editCourse && this.props.editCourse.assignments.map(assignment => {
      return (
      <th 
        key={assignment.id} 
        id={assignment.id}
        style={{width: '8%'}} 
        title={assignment.name + "-" + assignment.totalPoints} 
        scope="col">{assignment.name} <br/> {assignment.totalPoints}</th>
        )
    })



    return (
      <div className="my-2">
        <table class="table table-hover table-sm">
          <thead className="thead-light">
            <tr>
              <th scope="col"></th>
              {colHeaders}
            </tr>
          </thead>
          <tbody>
            <tr>

            </tr>
            <tr>
              <th scope="row">2</th>
              <td>Jacob</td>
              <td>Thornton</td>
              <td>@fat</td>
            </tr>
            <tr>
              <th scope="row">3</th>
              <td colspan="2">Larry the Bird</td>
              <td>@twitter</td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}


const mapStateToProps = state => ({
  editCourse: state.temp.editCourse,
  temp: state.temp.grid,
})

export default connect(mapStateToProps, {})(TablePractice);
