import React, { Component, useState } from 'react';
import {
    BrowserRouter as Router,
    NavLink,
    Switch,
    Route,
    Link,
    Redirect
} from "react-router-dom";
import LoginPage from '../../pages/login/LoginPage';
import axios from 'axios';


class LoginBox extends Component {
    constructor(props) {
        super()
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            email: "",
            password: ""
        }
    }

    setEmail(value) {
        this.setState({
            email: value
        })
    }

    setPassword(value) {
        this.setState({
            password: value
        })
    }

    handleSubmit() {
        //console.log(this.state.email)
        if (this.state.email.length > 0 && this.state.password.length > 0) {
        
            const user = {
                "email": this.state.email,
                "password": this.state.password
            }
            axios.post("http://localhost:8080/login", user )
            .then((response) => {
                if (response.data != "") {
                    sessionStorage.setItem("user", response.data);
                    console.log("SUCCESS");
                    switch (response.data.type) {
                        case "TEACHER":
                            console.log("TEACHER PAGE");
                            return history.push
                            break;
                    
                        default:
                            break;
                    }
                    
                } else {
                    sessionStorage.clear()
                    console.log("FAILED");
                }
                
                //console.log(response.data.user);
                //console.log(response.status);
                //console.log(response.statusText);
                //return <Redirect to="/admin" />
            }).catch()
            
            


        } else {
            console.log("FAIL")
        }
    }

    render() {

   
        return (
        
                <div className="mt-5 mx-auto w-50 p-3">
                    <div className="rounded-lg shadow p-3 bg-light">

                        <ul className="nav nav-tabs">
                            <li className="nav-item">

                                <NavLink activeClassName="active" className="nav-link" to="/login-student">Student</NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink activeClassName="active" className="nav-link" to="/login-parent">Parent</NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink activeClassName="active" className="nav-link" to="/login-staff">Staff</NavLink>
                            </li>
                        </ul>
                        <div>
                            <form>
                                <div class="form-group mt-4">
                                    <label for="exampleInputEmail1">Email address</label>

                                    <input
                                        type="email"
                                        className="border-top-0 border-left-0 border-right-0 rounded-0 form-control"
                                        value={this.state.email}
                                        autoFocus
                                        onChange={e => this.setEmail(e.target.value)}
                                        id="email"
                                        aria-describedby="Email Address" />
                                    <small id="emailHelp" className="form-text text-muted"></small>
                                </div>
                                <div class="form-group">
                                    <label for="exampleInputPassword1">Password</label>
                                    <input
                                        type="password"
                                        className="border-top-0 border-left-0 border-right-0 rounded-0 form-control"
                                        value={this.state.password}
                                        onChange={e => this.setPassword(e.target.value)}
                                        id="password" />
                                </div>
                                <div className="form-group">
                                    <button type="button" class="btn btn-primary w-100" onClick={this.handleSubmit}>Submit</button>
                                </div>
                                <a href="#" className="text-muted"><small>Register</small></a><a className="float-right text-muted font-size" href="#"><small>Need help?</small></a>

                            </form>
                        </div>

                    </div>
                </div>

              


        );
    }
}

export default LoginBox;
