import React, { Component } from 'react';
import DefaultImage from '../../../../images/default_user.png';

class UserInformation extends Component {
    render() {

        const obj = this.props.user.phone;
        const phoneList = []
        console.log(Array.isArray(obj) + "Is Item an Array")
        if (Array.isArray(obj)) {
            obj.forEach()
        }
        // const phone = this.props.user.phone.map(number => {
        //     return <p className="text-muted text-center">{number}</p>;
        // })
        const address = this.props.user.address ? this.props.user.address : <a href="#addressModal">Add an Address </a>;
        const picture = this.props.user.picture ? this.props.user.picture : DefaultImage;
        console.log(phoneList)
        return (
            <div>
                <div className="row p-4">
                    <div className="col-md-4 border border-left pt-4">
                        <div className="row text-center pb-3">
                        <img src={picture} width="100" height="100" className="border rounded-circle mx-auto" />
                        </div>
                        <div className="row">
                            <div className="col">
                                <h6 className="text-muted text-center">{this.props.user.firstName} {this.props.user.lastName}</h6>
                                <hr></hr>
                                <p className="text-muted text-center">{address}</p>
                                <p className="text-muted text-center">{this.props.user.email}</p>
                                {phoneList}
                            </div>
                        </div>
                    </div>
                    <div className="col-md-8">

                    </div>

                </div>
            </div>
        );
    }
}

export default UserInformation;
