import React, { Component } from 'react';

import TeacherLayout from './TeacherLayout';
//import './teacher_page.css';
import Header from './../../components/main_page/header/Header';
import axios from 'axios';

class TeacherPage extends Component {
    constructor() {
        super()
        this.retireveUserData = this.retireveUserData.bind(this)
        this.state = {
            user: "",
            message: ""
        }
    }

    componentDidMount() {
       
        this.retireveUserData(sessionStorage.getItem("user"))

    }

    retireveUserData(email) {

        axios.post("http://localhost:8080/find", {email})
            .then((response) => {
                console.log
                if (response.data != "") {
                    this.setState({
                        user: response.data
                    })
                } else {
                    sessionStorage.clear()
                    this.setState({
                        message: "Error retrieving data, please try again"
                    })
                }
            }
            )}
        


    render() {
        return (
            <div className="">
                <Header user={this.state.user}/>
                <TeacherLayout user={this.state.user} />
                <div class="alert alert-warning" role="alert">
                    {this.state.message}
                </div>
            </div>
        );
    }
}

export default TeacherPage;
