import React, { Component } from 'react';
import { connect } from 'react-redux'

class UserDetails extends Component {
    constructor() {
        super()
        this.state = {

        }
    }

    onChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        const date = ""

        const user = this.props.editUser && this.props.editUser.user

        return (
            <div>
                <form onSubmit={this.handleSubmit} id="form-to-clear">

                    <div className="form-row w-100">
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="email">Email address</label>
                            <input
                                onChange={e => this.onChange}
                                type="email"
                                name="email"
                                className="form-control w-100"
                                id="email"
                                value={this.state.email ? this.state.email : null}
                                placeholder={user ? user.email : "Enter email"}
                            />

                        </div>
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="password">Password</label>
                            <input
                                onChange={e => this.onChange}
                                type="password" name="password"
                                className="form-control w-100"
                                id="password"
                                value={this.state.password ? this.state.password : null}
                                placeholder={user ? user.password : "Password"}
                            />

                        </div>
                    </div>

                    <div className="form-row w-100">

                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="password">First Name</label>
                            <input
                                value={this.state.firstName ? this.state.firstName : null}
                                onChange={e => this.onChange}
                                type="text"
                                name="firstName"
                                className="form-control w-100"
                                id="firstName"
                                aria-describedby="emailHelp"
                                placeholder={user ? user.firstName : "First Name"}
                            />
                        </div>

                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="password">Last Name</label>
                            <input
                                value={this.state.lastName ? this.state.lastName : null}
                                onChange={e => this.onChange}
                                type="text" name="lastName"
                                className="form-control w-100"
                                id="lastName"
                                placeholder={user ? user.lastName : "Last Name"} />
                        </div>
                    </div>

                    <div className="form-group col">
                        <label for="inputAddress2">Date of Birth</label>
                        <input
                            onChange={e => this.onChange}
                            value={this.state.dob}
                            type="date" className="form-control"
                            id="inputAddress2"
                            name="dob"
                            placeholder={user ? user.dob : ""} />

                    </div>

                    {/* Place ability to add and edit addresses here, either through a new modal or an existing one */}




                    <div className="form-row">
                        <div className="col">
                            <button onClick={this.showAddress} className="btn btn-info w-100 mt-4">Add/Edit Address</button>
                        </div>
                        <div className="col">
                            <input type="submit" className="btn btn-primary w-100 mt-4">
                            </input>
                        </div>
                    </div>
                </form>

            </div>
        );
    }
}

const mapStateToProps = state => ({
    editUser: state.editUser,
    editUserType: state.editUserType
})


export default connect(mapStateToProps, {})(UserDetails);
