import { axios } from 'axios';
//Reducer is a switch statement with actions and defines those actions

//state is the initial state 
const getAdminList = () => {
        axios.get("http://localhost:8080/admin/all").then(response => {
            return response.data
        })
}

//Is it better to return the list from the API call and set state to that, or update state on my own
const adminReducer = (state = getAdminList(), action) => {
    switch (action.type) {
        case 'ADD':
            return state;
        case 'REMOVE':
            if(Array.isArray(state)) {
                state.push(action.payload)
            }
            return null;
        case 'UPDATE':
            return null;
        case "ALL":
            return state;
        default:
            break;
    }
} 

export default adminReducer;
