import React, { Component } from 'react';
import { connect } from 'react-redux'
import Fade from 'react-reveal/Fade'
import {editStudent } from './../../../../action/studentActions';
import { editAdmin } from './../../../../action/adminActions';
import { editTeacher } from './../../../../action/teacherActions';
import { editParent } from './../../../../action/parentActions';

class UserDetails extends Component {
    constructor() {
        super()
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            email: "",
            password: "",
            firstName: "",
            lastName: "",
            dob: ""
        }
    }

    handleSubmit(e) {
        e.preventDefault()

        const type = this.props.editUserType

        const userDetails = Object.assign(this.props.editUser.user,
            { firstName: this.state.firstName },
            { lastName: this.state.lastName },
            { dob: this.state.dob },
            { email: this.state.email },
            { password: this.state.password } );

        const user = {
            user: {
                id: this.props.editUser.user.id,
                firstName: this.state.firstName ? this.state.firstName : this.props.editUser.user.firstName,
                lastName: this.state.lastName ? this.state.lastName : this.props.editUser.user.lastName,
                dob: this.state.dob ? this.state.dob : this.props.editUser.user.dob,
                email: this.state.email ? this.state.email : this.props.editUser.user.email,
                lastName: this.state.lastName ? this.state.lastName : this.props.editUser.user.lastName

            }
        }

        console.log(this.props.editUser)
        console.log(user)

        switch (type) {
            case 'parent':
                this.props.editParent(user);
                break;
            case 'student':
                console.log("STUDENT")
                this.props.editStudent(user);
                break;
            case 'admin':
                this.props.editAdmin(user);
                break;
            case 'teacher':
                this.props.editTeacher(user);
                break;
            default:
                break;
        }

        
    }

    onChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        console.log(this.props.editUser)
        let date = ""
        const user = this.props.editUser && this.props.editUser.user
 
        if (user && user.dob) {
            let temp = new Date(Date.parse(user.dob))
            date = temp.toISOString()
        }
        return (
            <div>
                <form onSubmit={this.handleSubmit} id="form-to-clear">

                    <div className="form-row w-100">
                        <Fade left>
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="email">Email address</label>
                            <input
                                onChange={e => this.onChange}
                                type="email"
                                name="email"
                                className="form-control w-100"
                                id="email"
                                value={this.state.email ? this.state.email : null}
                                placeholder={user ? user.email : "Enter email"}
                            />

                        </div>
                        </Fade>
                        <Fade right>
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="password">Password</label>
                            <input
                                onChange={e => this.onChange}
                                type="password" name="password"
                                className="form-control w-100"
                                id="password"
                                value={this.state.password ? this.state.password : null}
                                placeholder={user ? user.password : "Password"}/>
                        </div>
                        </Fade>

                    </div>

                    <div className="form-row w-100">
                    <Fade top>
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="password">First Name</label>
                            <input
                                value={this.state.firstName ? this.state.firstName : null}
                                onChange={e => this.onChange}
                                type="text"
                                name="firstName"
                                className="form-control w-100"
                                id="firstName"
                                aria-describedby="emailHelp"
                                placeholder={user ? user.firstName : "First Name"}/>
                        </div>
                        </Fade>
                        <Fade bottom>
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="password">Last Name</label>
                            <input
                                value={this.state.lastName ? this.state.lastName : null}
                                onChange={e => this.onChange}
                                type="text" name="lastName"
                                className="form-control w-100"
                                id="lastName"
                                placeholder={user ? user.lastName : "Last Name"} />
                        </div>
                        </Fade>
                    </div>
                    <div className="form-row w-100">
                        <Fade left>
                    <div className="form-group col-md-6 w-100 pl-0">
                        <label for="inputAddress2">Date of Birth</label>
                
                        <input
                            onChange={e => this.onChange}
                            value={this.state.dob}
                            type="date" className="form-control"
                            id="inputAddress2"
                            name="dob"
                            placeholder={date ? date : ""} />

                    </div>
                    </Fade>
                    {/* 
                    
                    <div className="form-group col-md-6 w-100 pl-0">
                        <label for="inputAddress2">Picture</label>
                        <input
                            onChange={e => this.onChange}
                            value={this.state.picture}
                            type="file" className="form-control"
                            id="inputAddress2"
                            name="picture"
                            placeholder={user ? user.picture : ""} />

                    </div> */}
                    </div>
                    <Fade right>
                    <div className="form-row">
                        <input type="submit" className="btn btn-primary w-100 mt-4"></input>
                    </div>
                    </Fade>
                </form>

            </div>
        );
    }
}

const mapStateToProps = state => ({
    editUser: state.temp.editUser,
    editUserType: state.temp.editUserType
})


export default connect(mapStateToProps, { editTeacher, editParent, editStudent, editAdmin})(UserDetails);
