import React, { Component, useEffect } from 'react';
import Delete from '../../../images/icons/trash.svg';
import Edit from '../../../images/icons/edit.svg';
import saveUser from '../../databaseUtil/databaseUtil';
import axios from 'axios'
import EditUser from './EditUser';
import UserListing from './UserListing';
import AddUserForm from './AddUserForm';

class AddUserModal extends Component {
  constructor() {
    super()
    

    this.editUser = this.editUser.bind(this)
    this.deleteUser = this.deleteUser.bind(this)

    this.state = {
      currentEditUserAddress: [],
      currentEditUser: "",
      success: "",
      userList: [],
      userCompoentList: [0]
    }
  }

  editUser(user) {

    this.setState({
      currentEditUser: user,
      currentEditUserAddress: user.contactInformation
    })
  }

  deleteUser(id) {
    //delete from database and reset state
    const answer = window.confirm("Are you sure you want to delete user " + id );

    if(answer) {
      axios.get("http://localhost:8080/delete/" + id).then(response => {
      console.log(response.data)
      return response.data
    })
    
    const tempItems = this.state.userList.map(user => {

      return user.key == id ? null : user
    })

    this.setState({
      userList: tempItems
    })
    } else {  
      return 
    }

  }

  getAllUsers = async () => {
    let response = await axios.get("http://localhost:8080/all");
    let { data } = response.data;
    this.setState({ userList: data });
  };


  componentDidMount() {

    this.getAllUsers()


    console.log(this.state.userList)

    const tempUserComponentList = this.state.userList.map(user => {
      const fullName = user.id + " " + user.firstName + " " + user.lastName
      console.log(fullName)
      return (
        <UserListing 
          key={user.id}
          editUser={()=> this.editUser(user)} 
          deleteUser={()=> this.deleteUser(user.id)}
          text={fullName} />
      )
    })

    this.setState({
      userCompoentList: tempUserComponentList
    })



          

  }

  render() {
    let counter = 1
    return (
      <div>
        <div data-backdrop="static" className="modal fade" id="addStudentModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog mw-100 w-75" role="document">
            <div className="modal-content">
              <div className="modal-header" ref={this.focusRef}>
                <h5 className="modal-title" id="exampleModalLabel">Add or Remove Users</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">×</span>
                </button>
              </div>
              <div className="modal-body">
                <div>
                  <div className="row pl-2 border-bottom">
                    <form className="form-inline" >
                      <div className="input-group mb-2 mr-sm-2">
                        <div className="input-group-prepend">
                          <div className="input-group-text">Search</div>
                        </div>
                        <input type="text" className="form-control" id="inlineFormInputGroupUsername2" placeholder="Username" />
                      </div>
                      {/* Select user dropdown menu */}
                      <div className="input-group mb-2 mr-sm-2">
                        <select className="custom-select my-1 mr-sm-2">
                          <option selected>Choose...</option>
                          <option value={1}>Student</option>
                          <option value={2}>Admin</option>
                          <option value={3}>Parent</option>
                          <option value={4}>Teacher</option>
                        </select>
                      </div>

                      <button type="submit" className="btn btn-outline-primary mb-2">Find</button>
                    </form>

                  </div>
                  <div className="row">
                    <div className="col-md-3 border-right overflow-auto mt-4">

                      {/* List of all users that have been searched for */}

               {this.state.users.length === 0 ? (
                   <div>Loading...</div>
               ) : (
                   this.state.users.map((e, i) => {
                       return <div key={i}>{e.first_name}</div>;
                    })
               )}
          
                      {this.state.userComponentList.length === 0 ? (
                        <div class="progress">
                          <div class="progress-bar progress-bar-striped progress-bar-animated" role="progressbar" aria-valuenow={()=> counter++} aria-valuemin="0" aria-valuemax="100" style="width: 75%"></div>
                        </div>
                        
                      ) : (
                        this.state.userCompoentList
                      )}

                    </div>
                    <div className="col mt-4">
                      <div className="col-md-12">
                        
                        {/* Form to create a new student */}
                        <AddUserForm />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <h4 className="text-success mr-5">{this.state.success}</h4>
                <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
              </div>
            </div>
          </div>
        </div>
        
      </div>
    );
  }
}

export default AddUserModal;
