import React, { Component, useEffect } from 'react';
import axios from 'axios'
import UserListing from './UserListing';
import AddUserForm from './AddUserForm';

class AddUserModal extends Component {
  constructor() {
    super()

    this.state = {
      userList: [],
      result: "",
      search: "",
      type: ""
    }
  }


  handleSearchChange(value) {
    this.setState({
      search: value
    })
  }

  handleTypeChange(value) {
    this.setState({
      type: value
    })
  }

  deleteUser(u) {
    const user = {
      id: u.id
    }
    if(window.confirm("Are you sure you want to delete " + u.firstName + " " + u.lastName)) {
      
      axios.post("http://localhost:8080/delete", user).then(response => {
        console.log(u.id)
        console.log(response.data)
        const currentUser = response.data
        
        if (currentUser.id === u.id) {
          let tempUserList = this.state.userList
          const index = tempUserList.findIndex(tempUser => {
            return tempUser.id === u.id
          })
  
          tempUserList.splice(index,1)
  
          this.setState({
            userList: tempUserList
          })
  
        } else {
          console.log("error")
        }
      })
    } else {
      console.log("Canceled")
    }

    
  }

  saveUser(user) {
    if (!user.type) {
      return "User type not defined"
    }
    let path = ""
    switch (user.type) {
      case "TEACHER":
        path = "teacher/new"
        break;
      case "PARENT":
        path = "parent/new"
        break;
      case "STUDENT":
        path = "student/new"
        break;
      case "ADMIN":
        path = "admin/new"
        break;
      default:

        break;
    }
    const fullPath = "http://localhost:8080/" + path
    axios.post(fullPath, user).then((response) => {
      let tempUserList = this.props.userList
      tempUserList.push(response.data)

      //Instead of setting state, we need to send the data back
      this.props.updateUserList(tempUserList)
      // this.setState({
      //   userList: tempUserList
      // })
    })
  }

  // getAllUsers = async () => {
  //   let response = await axios.get("http://localhost:8080/all");
  //   let data = response.data;
  //   this.setState({ userList: data });
  // };

  componentDidUpdate(prevProps) {
    const user = this.props.changedUser
    const tempList = this.state.userList.map(u=> {
      if (u.id == user.id) {
        return user  
      } else {
        return u
      }
    })

    if (this.props.changedUser.id !== prevProps.changedUser.id) {
      this.setState({
        userList: tempList
      })
    }
    
  }

  componentWillMount() {
    this.setState({
      userList: this.props.userList
    })
  }

  componentWillUpdate() {
    
  }

  componentWillReceiveProps() {
    
  }

  componentDidMount() {
    
  }

  render() {
    let tempList = []
    let counter = 1
    return (
      <div>
        <div data-backdrop="static" className="modal fade" id="addStudentModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog mw-100 w-75" role="document">
            <div className="modal-content">
              <div className="modal-header" ref={this.focusRef}>
                <h5 className="modal-title" id="exampleModalLabel">Add or Remove Users</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">Ã—</span>
                </button>
              </div>
              <div className="modal-body">
                <div>
                  <div className="row pl-2 border-bottom">

                    {/* Top Search Bar */}

                    <form className="form-inline" >
                      <div className="input-group mb-2 mr-sm-2">
                        <div className="input-group-prepend">
                 
                          <div  className="input-group-text">Search</div>
                        </div>
                        <input onChange={e => this.handleSearchChange(e.target.value)} type="text" className="form-control" id="inlineFormInputGroupUsername2" placeholder="Username" />
                      </div>
                      {/* Select user dropdown menu */}
                      <div  className="input-group mb-2 mr-sm-2">
                        <select onChange={e => this.handleTypeChange(e.target.value)} className="custom-select my-1 mr-sm-2">
                          <option selected>Choose...</option>
                          <option value={"student"}>Student</option>
                          <option value={"admin"}>Admin</option>
                          <option value={"parent"}>Parent</option>
                          <option value={"teacher"}>Teacher</option>
                        </select>
                      </div>

                      <button type="submit" className="btn btn-outline-primary mb-2">Find</button>
                    </form>



                  </div>
                  <div className="row">
                    <div className="col-md-3 border-right overflow-auto mt-4">

                      {/* List of all users that have been searched for */}
                      {this.props.userList.length === 0 ? (
                        <div class="progress">
                          <div class="progress-bar progress-bar-striped progress-bar-animated w-75" role="progressbar" aria-valuenow={() => counter++} aria-valuemin="0" aria-valuemax="100" ></div>
                        </div>
                      ) : (
                          tempList = this.props.userList.map(user => {
                            const fullName = user.id + " " + user.firstName + " " + user.lastName
                            const length = this.state.search.toLowerCase().length
                            let userType = "";
                            if(this.state.type == "" || this.state.type == "Choose...") {
                              userType = "all"
                            }

                            if (
                              ((this.state.search.toLowerCase() == user.firstName.substr(0, length).toLowerCase()) ||
                              (this.state.search.toLowerCase() == user.lastName.substr(0, length).toLowerCase()) ||
                              (this.state.search == user.id)))
                               {
                                 if (userType == "all") {
                                   return(
                                  <div>
                                  <UserListing
                                    key={user.id}
                                    user={user}
                                    editUser={()=> this.props.selectedUser(user)}
                                    deleteUser={()=> this.deleteUser(user)}
                                    text={fullName.length > 18 ? fullName.substr(0,10) +"..." : fullName} />
                                    </div>)
                                 } else if(this.state.type.toLowerCase() == user.type.toLowerCase()) {
                                   return (
                                  <div>
                                  <UserListing
                                    key={user.id}
                                    user={user}
                                    editUser={()=> this.props.selectedUser(user)}
                                    deleteUser={()=> this.deleteUser(user)}
                                    text={fullName.length > 18 ? fullName.substr(0,10) +"..." : fullName} />
                                    </div>)
                                 }
                              
                            }
                            
                          } )
                        ) }
          
                    </div>

                    <div className="col mt-4">
                      <div className="col-md-12">

                        {/* Form to create a new student */}
                        <AddUserForm handleSubmit={(user)=> this.saveUser(user)} />

                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <h4 className="text-success mr-5">{this.state.success}</h4>
                <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
              </div>
            </div>
          </div>
        </div>
  
      </div>
    );
  }
}

export default AddUserModal;
