import React, { Component } from 'react';
import { connect } from 'react-redux';

class AssignStudentForm extends Component {
    render() {

        const studentList = this.props.editCourse && this.props.editCourse.studentList && this.props.editCourse.studentList.map(student => {
            return <p key={student.id}>{student.user.firstName} {student.user.lastName}</p>
        })

        const length = this.props.editCourse && this.props.editCourse.studentList.length

        return (
            <div>
                <h5>{this.props.editCourse && this.props.editCourse.name}  
                {this.props.editCourse ? ("    |    Students: " + length ) : null }</h5>
                {studentList}
            </div>
        );
    }
}

const mapStateToProps = state => ({
    editCourse: state.temp.editCourse
})

export default connect(mapStateToProps, {})(AssignStudentForm);
