import React, { Component } from 'react';
import axios from 'axios'
import Fade from 'react-reveal/Fade'

class ContactInformation extends Component {
    constructor() {
        super()
        this.state = {
            address: "",
            unit: "",
            city: "",
            state: "",
            zipcode: "",
        }
    }

    onChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    componentDidMount() {
        axios.get("http://localhost:8080/getStates").then(response => {
            const temp = response.data.map(st => {
                return <option key={st}>{st}</option>
            })

            this.setState({
                states: temp
            })
        })
    }

    render() {
        return (
            <div>
                <form>
                    <div className="form-row">

                    
                    <div class="form-group col-md-8">
                            <label for="selectAddress">Select Address to Edit or Delete</label>
                            <select id="selectAddress" onChange={e => this.onChange} name="selectAddress"
                            class="form-control">

       
                            </select>
                    </div>
                    <div className="col-md-4">
                        <button className="btn btn-warning">Delete</button>
                    </div>
                    </div>
                    <div class="form-group">
                        <label for="inputAddress">Address</label>
                        <input type="text" onChange={e => this.onChange} name="address" value={this.state.address}
                        class="form-control" id="inputAddress" placeholder="1234 Main St" />
                    </div>
                    <div class="form-group">
                        <label for="inputAddress2">Address 2</label>
                        <input type="text" onChange={e => this.onChange} name="unit" value={this.state.unit}
                        class="form-control" id="inputAddress2" placeholder="Apartment, studio, or floor" />
                    </div>
                    <div class="form-row">
                        <div class="form-group col-md-6">
                            <label for="inputCity">City</label>
                            <input type="text" onChange={e => this.onChange} name="city" value={this.state.city}
                            class="form-control" id="inputCity" />
                        </div>
                        <div class="form-group col-md-4">
                            <label for="inputState">State</label>
                            <select id="inputState" onChange={e => this.onChange} name="state" value={this.state.state}
                            class="form-control">
                                {this.state.states && this.state.states.map(state => {
                                    return <option>{state}</option>
                                })}
       
                            </select>
                        </div>
                        <div class="form-group col-md-2">
                            <label for="inputZip">Zip</label>
                            <input type="text" onChange={e => this.onChange} name="zipcode" value={this.state.zipcode}
                            class="form-control" id="inputZip" />
                        </div>
                    </div>
                    <div class="form-row">
                        <div className="col">
                        <button type="button" class="btn btn-secondary w-100">Edit/Add Phone Number(s)</button>
                        </div>
                        <div className="col">
                        <button type="submit" class="btn btn-primary w-100">Submit</button>
                        </div>
                        
                    </div>
                </form>

            </div>
        );
    }
}

export default ContactInformation;
