import { GET_USER } from './types'
import axios from 'axios'


export function fetchPosts() {
    return function (dispatch) {
        //all fetch statements go here
    }
}

//dispatch is part of thunk, it sends the data off to the reducer
export const getAdminUser = (id) => dispatch => {
    axios.get("http://localhost:8080/admin/find/" + id)
        .then(response => {
            dispatch({
                type: GET_USER,
                payload: response.data
            })
        })
}

export const getTeacherUser = (id) => dispatch => {
    axios.get("http://localhost:8080/teacher/find/" + id)
        .then(response => {
            dispatch({
                type: GET_USER,
                payload: response.data
            })
        }

        )
}

export const editUserDetails = (user) => dispatch => {
    axios.post("http://localhost:8080/student/update/details", user)
        .then(response => {
            dispatch({
                type: UPDATE_USER_DETAILS,
                payload: response.data
            })
        })
}

export const addContactStudent = (student) => dispatch => {
    axios.post("http://localhost:8080/student/add/contact", student)
        .then(response => {
            dispatch({
                type: UPDATE_STUDENT,
                payload: response.data
            })

        })
}

export const deleteContactStudent = (contactId, userId) => dispatch => {
    axios.post(`http://localhost:8080/student/delete/contact?contactId=${contactId}&userId=${userId}`,)
        .then(response => {
            dispatch({
                type: DELETE_CONTACT_STUDENT,
                payload: contactId
            })

        })
}

export const deletePhoneStudent = (number, id) => dispatch => {
    axios.post(`http://localhost:8080/student/delete/phone?number=${number}&id=${id}`)
        .then(response => {
            dispatch({
                type: DELETE_PHONE_STUDENT,
                payload: number
            })

        })
}

export const addPhoneStudent = (number) => dispatch => {
    axios.post("http://localhost:8080/student/add/contact", number)
        .then(response => {
            dispatch({
                type: UPDATE_STUDENT,
                payload: response.data
            })

        })
}
