import React, { Component } from 'react';
import axios from 'axios'

class CreateAssignmentForm extends Component {
    constructor() {
        super()
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            assignmentTypes: [],
            name: "",
            points: "",
            dueDate: "",
            type: ""
        }
    }

    onChange(e) {
        
    }

    handleSubmit(event) {
        event.preventDefault()
        if (!this.props.selectedCourse.id) {
            alert("Please select a course")
        } else {
            console.log(this.props.selectedCourse.id)
            const assignment = {
                name: this.state.name,
                description: this.state.description,
                dueDate: this.state.dueDate,
                totalPoints: this.state.points,
                assignmentType: this.state.type ? this.state.type : document.getElementById("assignmentType").value,
                course: this.props.selectedCourse
            }
            
            axios.post(`http://localhost:8080/course/assignment/new?courseId=${this.props.selectedCourse.id}`, assignment).then(response => {
                console.log(response.data)
            })
        }
    }

    componentDidMount() {
        axios.get("http://localhost:8080/assignment/getTypes").then(response => {
            this.setState({
                assignmentTypes: response.data
            })
        })
    }

    render() {
        let date = "";

        const assignmentTypes = this.state.assignmentTypes.map(type => {
            return <option value={type}>{type}</option>
        })

        return (

            <form className="w-100 p-3" onSubmit={this.handleSubmit}>
                {/* due date, description, course */}
                <div className="row py-1">
                    <div className="col">
                        <label htmlFor="name">Name</label>
                        <input
                            onChange={e => this.setName(e.target.value)}
                            type="text"
                            //value={this.state.name}
                            name="name"
                            className="form-control"
                            placeholder="Assignment Name" />
                    </div>
                    <div className="col">
                        <label htmlFor="assignmentType">Assignment Type</label>
                        <select onChange={e => this.setType(e.target.value)} className="custom-select" name="assignmentType" id="assignmentType">
                            {assignmentTypes}
                        </select>
                    </div>
                </div>

                <div className="row py-1">
                    <div className="col">
                        <label htmlFor="points">Points</label>
                        <input
                            onChange={e => this.setPoints(e.target.value)}
                            type="text"
                            name="points"
                            //value={this.state.points}
                            className="form-control"
                            placeholder="Total Points" />
                    </div>
                    <div className="col">
                        <label htmlFor="dueDate">Due Date</label>
                        <input
                            onChange={e => this.setDueDate(e.target.value)}
                            //value={date ? date.substr(0, 10) : null}
                            type="date" className="form-control"
                            id="inputAddress2"
                            name="dueDate"
                            onBlur={e => this.setDueDate(e.target.value)}
                            placeholder={date ? date.substr(0, 10) : ""} />
                    </div>
                </div>
                <div className="row py-1">
                    <div className="col">
                        <label htmlFor="description">Description</label>
                        <input
                            onChange={e => this.setDescription(e.target.value)}
                            //value={this.state.description}
                            type="text"
                            className="form-control"
                            id="description"
                            name="description"
                            placeholder="Assignment Description"
                        />
                    </div>
                    <div className="col-md-2">
                        <label htmlFor="description"></label>
                        <input className="btn btn-primary mt-2" type="submit" />
                    </div>
                </div>
            </form>
        );
    }
}

export default CreateAssignmentForm;
