import React, { Component } from 'react';
import Notification from './Notification';
import './notification.css';
import { connect } from 'react-redux'

class NotificationBox extends Component {


    shuffle(array) {
        array.sort(() => Math.random() - 0.5)
    }

    render() {
        //Needs to get all messages, needs to get all assignments
        

        const array = this.props.messages && this.props.messages.concat(this.props.assignments)
        array && array.sort(() => Math.random() - 0.5)
        console.log(array)


        const notificationItem = array && array.map(notification => {
            return (
                <Notification 
                links="place a link object in here" 
                title="Missing Assignment" 
                location="English 3" 
                details="Reading Assignment 3.  This assignment is worth 25 points of which you have currently earned 0" />
            )
        })





        return (

            <div class="jumbotron m-3 shadow p-3 z-1030">
                <h6 className="">Notifications</h6>
                <div id="carouselExampleInterval" class="carousel slide" data-ride="carousel">
                    <div className="carousel-inner">
                        <div className="carousel-item active" data-interval="5000">
                            <div className="row mx-auto">
                                <Notification links="place a link object in here" title="Missing Assignment" location="English 3" details="Reading Assignment 3.  This assignment is worth 25 points of which you have currently earned 0" />
