import React, { Component } from 'react';
import LoginPage from '../pages/login/LoginPage'
import StudentPage from './../pages/student/StudentPage';
import AdminPage from '../pages/admin/AdminPage';
import {
    BrowserRouter as Router,
    NavLink,
    Switch,
    Route,
    Link,
    Redirect
  } from "react-router-dom";
import ProtectedLink from '../lib/ProtectedLink';


class Layout extends Component {
    render() {

        localStorage.setItem("userToken", false);
        localStorage.setItem("userType", "ADMIN");

        const token = localStorage.getItem("userToken")
        const userType = localStorage.getItem("userType")
        let element = <Redirect to="/" component={LoginPage} />
        let component = LoginPage;
        let to = "/";
        console.log(token)
        if (token) {
            switch (userType) {
                case "STUDENT":
                    element = <Route exact 
                    path="/">
                    {loggedIn ? <Redirect 
                    to="/dashboard" /> : <PublicHomePage />}
                    </Route>
                    break;
                case "TEACHER":
                    to = "/teacher-home";
                    break;
                case "ADMIN":
                    element = <Redirect to="admin-home" component={AdminPage} />
                    component = AdminPage;
                    break;
                case "PARENT":
                    to = "/parent-home";
                    break;
                default:
                    to = "/";
                    component = LoginPage;
                    break;
            }
        } else {
            
        }

        return (
            <Router>
                <div>
                    <Switch>
                        <Route path={["/", "login-student", "login-parent", "login-staff", "login", "home"]} exact component={LoginPage} />
                        {element}
                    </Switch>
                    
                </div>
            </Router>
        );
    }
}

export default Layout;
