import React, { Component } from 'react';
import { HotTable } from '@handsontable/react'
import 'handsontable/dist/handsontable.full.css';
import 'handsontable/dist/handsontable.full.css'
import { connect } from 'react-redux';

class Table extends Component {
    constructor() {
        super()
        this.data = [
            [, , , , ,]
        ]
    }

    negativeValueRenderer(instance, td, row, col, prop, value, cellProperties) {
        Handsontable.renderers.TextRenderer.apply(this, arguments);

        // if row contains negative number
        if (parseInt(value, 10) < 0) {
            // add class "negative"
            td.className = 'make-me-red';
        }

        if (!value || value === '') {
            td.style.background = '#EEE';
        }
        else {
            if (value === 'Nissan') {
                td.style.fontStyle = 'italic';
            }
            td.style.background = '';
        }
    }

    firstRowRenderer(instance, td, row, col, prop, value, cellProperties) {
        Handsontable.renderers.TextRenderer.apply(this, arguments);
        td.style.fontWeight = 'bold';
        td.style.color = 'green';
        td.style.background = '#CEC';
    }

    
    render() {
        
        let colHeaders = this.props.editCourse && this.props.editCourse.assignments.map(assignment => {
            return { id: assignment.id, value: assignment.name }
        })
        colHeaders && colHeaders.unshift("")

        let rowHeaders = this.props.editCourse && this.props.editCourse.studentList.map(student => {
            return <td>{student.user.firstName + " " + student.user.lastName}</td>
        })


        const settings = {
            licenseKey: 'non-commercial-and-evaluation',
            data: this.data,
            colHeaders: colHeaders,
            rowHeaders: rowHeaders,
            colWidths: 100,
            // width: "600", 
            // height: "300",
            headerTooltips: {
                rows: true,
                columns: true,
                onlyTrimmed: false
            }
        }

        return (
            <div id="hot-app" className="table table-hover table-sm">
                <HotTable id="example1" className="my-3" settings={settings} />
            </div>
        );
    }
}

const mapStateToProps = state => ({
    editCourse: state.temp.editCourse,
    temp: state.temp.grid,
})

export default connect(mapStateToProps, {})(Table)
