import React, { Component } from 'react';
import './modal.css'

class EditUser extends Component {
    constructor(props) {
        
        super()
        //this.handleSubmit = this.handleSubmit.bind(this);

        this.state = {
            dob: "",
            firstName: "",
            lastName: "",
            email: "",
            password: "",
            address: "",
            unit: "",
            city: "",
            state: "",
            zipcode: "",
        }
    }

    setDob(value) {
        this.setState({
          dob: value
        })
      }
    
      setEmail(value) {
          
        this.setState({
          email: value
        })
      }
    
      setPassword(value) {
        this.setState({
          password: value
        })
      }
    
      setFirstName(value) {
        this.setState({
          firstName: value
        })
      }
    
      setLastName(value) {
        this.setState({
          lastName: value
        })
      }
    
      setAddress(value) {
        this.setState({
          address: value
    
        })
      }
    
      setUnit(value) {
        this.setState({
    
          unit: value
    
        })
      }
    
      setCity(value) {
        this.setState({
    
          city: value
    
        })
      }
    
      setUserState(value) {
        this.setState({
    
          state: value
    
        })
      }
    
      setZipcode(value) {
        this.setState({
    
          zipcode: value
    
        })
      }
    
      setType(value) {
    
        this.setState({
          type: value
        })
      } 

      componentWillMount() {
        
        
      }
      com
      componentDidUpdate() {
        
        console.log(this.state.email)
      }

      componentWillReceiveProps() {
        
      }

    //   componentDidMount() {
    //     this.setState({
    //         dob: this.props.user.dob,
    //         firstName: this.props.user.firstName,
    //         lastName: this.props.user.lastName,
    //         email: this.props.user.email,
    //         password: this.props.user.password,
    //         address: this.props.user.contactInformation.address,
    //         unit: this.props.user.contactInformation.unit,
    //         city: this.props.user.contactInformation.city,
    //         state: this.props.user.contactInformation.state,
    //         zipcode: this.props.user.contactInformation.zipcode,
    //       })
        
    //   }


    render() {


        return (
            <div>
                <div className="modal fade modal-bg-color" id="editUser" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog mw-75 w-75" role="document">
                        <div className="modal-content">
                            <div className="modal-header" ref={this.focusRef}>
                                <h5 className="modal-title" id="exampleModalLabel">Add or Remove Users</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">×</span>
                                </button>
                            </div>
                            <div className="modal-body p-5">
                                <form onSubmit={this.handleSubmit} id="form-to-clear">
                                    <div className="form-row w-100">
                                        <div className="form-group col-md-6 w-100">
                                            <label htmlFor="email">Email address</label>
                                            <input 
                                            onChange={e => this.setEmail(e.target.value)} 
                                            type="email" 
                                            name="email" 
                                            className="form-control w-100" 
                                            id="email" 
                                            aria-describedby="emailHelp"
                                            value={this.state.email ? this.state.email : null} 
                                            placeholder={this.state.email ? this.state.email : "Enter email"} 
                                            required />
             
                                        </div>
                                        <div className="form-group col-md-6 w-100">
                                            <label htmlFor="password">Password</label>
                                            <input 
                                            onChange={e => this.setPassword(e.target.value)} 
                                            type="password" name="password" 
                                            className="form-control w-100" 
                                            id="password" 
                                            value={this.state.password ? this.state.password : null} 
                                            placeholder={this.state.email ? this.state.email : "Password"} 
                                            required />
          
                                        </div>
                                    </div>

                                    <div className="form-row w-100">

                                        <div className="form-group col-md-6 w-100">
                                            <label htmlFor="password">First Name</label>
                                            <input required 
                                            value={this.state.firstName ? this.state.firstName : null}  
                                            onChange={e => this.setFirstName(e.target.value)} 
                                            type="text" 
                                            name="firstName" 
                                            className="form-control w-100" 
                                            id="firstName" 
                                            aria-describedby="emailHelp" 
                                            placeholder={this.state.password ? this.state.password : "First Name"}  />
                                        </div>

                                        <div className="form-group col-md-6 w-100">
                                            <label htmlFor="password">Last Name</label>
                                            <input required 
                                            value={this.state.lastName ? this.state.lastName : null}  
                                            onChange={e => this.setLastName(e.target.value)} 
                                            type="text" name="lastName" 
                                            className="form-control w-100" 
                                            id="lastName" 
                                            placeholder={this.state.password ? this.state.password : "Last Name"}  />
                                        </div>
                                    </div>

                                    <div className="form-group col">
                                        <label for="inputAddress2">Date of Birth</label>
                                        <input required value={e => Date(this.props.user.dob)} onChange={e => this.setDob(e.target.value)} type="date" className="form-control" id="inputAddress2" name="unit" placeholder="Apartment, studio, or floor" />

                                    </div>

                                    <div class="input-group col">
                                        <div class="input-group-prepend">
                                            <button class="btn btn-outline-primary" type="button" id="button-addon1">Edit</button>
                                        </div>
                                        <select id="type" onChange={e => this.setType(e.target.value)} className="custom-select form-control" name="type" required>
                                            {Addresses}

                                        </select>
                                    </div>


                                    <div className="form-row">

                                        <div className="col">

                                            <input type="submit" className="btn btn-primary w-100 mt-4">

                                            </input>

                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <h4 className="text-success mr-5">{this.state.success}</h4>
                                <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
                            </div></div>
                    </div>
                </div>
            </div>

        );
    }
}

export default EditUser;
