import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addStudentToList } from './../../../../action/tempActions';

class StudentItem extends Component {
    constructor() {
        super()
        this.handleAddStudent = this.handleAddStudent.bind
        this.state = {
            checked: false
        }
    }

    handleAddStudent(currentStudent) {
        if (!this.state.checked) {
            this.setState({
                checked: true
            })
            this.props.addStudentToList(currentStudent)
        } else {
            //this.props.removeStudentFromList(currentStudent)
            this.setState({
                checked: false
            })
        }
    }

    render() {
        // Pop up a little green check mark when clicked, take it away when unclicked
        return (
            <button type="button" className="btn btn-info"
            onClick={() => this.handleAddStudent(this.props.currentStudent)}>
                {this.props.name}
            </button>
        );
    }
}

const mapStateToProps = state => ({

})

export default connect(mapStateToProps, { addStudentToList })(StudentItem);
