import { GET_USER, HOLD_EDIT_USER, UPDATE_USER } from '../action/types'

const initialState = {
    user: {}
}

export default function(state = initialState, action) {
    switch (action.type) {
        case UPDATE_USER:
            tempStudentList = state.students && state.students.map(student => {
                
                if (student.id == action.payload.id) {
                    return action.payload
                } else {
                    return student
                }
            })
            return {
                ...state,
                students: tempStudentList
            };
        case GET_USER:
            return {
                ...state,
                user: action.payload
            }
        case 'REMOVE':
            return state;
        default:
            return state;
    }
}
