import { GET_USER, HOLD_EDIT_USER } from '../../action/types'

const initialState = {
    user: {}
}

export default function(state = initialState, action) {
    switch (action.type) {
        case HOLD_EDIT_USER:
            return {
                ...state,
                holdEditUser: action
            }
        case GET_USER:
            return {
                ...state,
                user: action.payload
            }
        case 'REMOVE':
            return state;
        default:
            return state;
    }
}
