import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import Layout from './layout/Layout'
import { Provider } from 'react-redux' //connects store(global state) to entire app




//Store Globalized State
const store = createStore(
  reducerIndex,
  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__(), applyMiddleware())


//Parts of a Redux Application
//Action (a function that returns an object)


//Reducer - how do actions transform state into next state
//2 parameters, initial state, action (piece of state)


//Display


//Dispatch



ReactDOM.render(
  <Provider store={store}>
    <Layout />
  </Provider>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
