import React, { Component } from 'react';
import axios from 'axios'
import CourseItem from '../addCourse/CourseItem';
import Bounce from 'react-reveal/Bounce'
import AssignStudentForm from './AssignStudentForm';
import StudentItem from './StudentItem';
import { connect } from 'react-redux'

class AssignStudentModal extends Component {
    constructor() {
        super()
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            search: "",
            selectedCourse: {},
            studentList: []
        }
    }

    handleSubmit() {
        let course = this.state.selectedCourse
        course.studentList = this.state.studentList

        axios.post("http://localhost:8080/course/update/students", course).then(response => {
            
        })
    }

    editCourse(course) {
        
        this.setState({
            selectedCourse: course
        })
    }

    selectStudent(checked, value) {

        if (checked) {
            let tempList = this.state.studentList
            tempList.push(value)
            this.setState({
                studentList: tempList
            })
        } else {
            let tempList = this.state.studentList
            const index = tempList.indexOf(value)
            tempList.splice(index, 1)

            this.setState({
                studentList: tempList
            })

        }
    }



    handleSearchChange(value) {
        this.setState({
            search: value
        })
    }

    render() {
        let counter = 1
        const tempCourses = this.props.courses && this.props.courses.courses
        const tempStudents = this.props.students && this.props.students.students
        const courseItems = tempCourses && tempCourses.map(course => {
            return <CourseItem course={course} name={course.name} selectCourse={() => this.editCourse(course)} />
        })

        const studentItems = tempStudents && tempStudents.map(student => {
            return <StudentItem selectStudent={e => this.selectStudent(e.target.checked, student)} student={student} name={student.firstName + " " + student.lastName} />
        })

        return (
            <div>
                <div className="modal fade" id="assignStudentModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog mw-100 w-75" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="exampleModalLabel">Assign Students</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">Ã—</span>
                                </button>
                            </div>
                            <div className="modal-body">

                                <div>
                                    <div className="row pl-2 border-bottom">

                                        {/* Top Search Bar */}

                                        <form className="form-inline ml-auto mr-2" >
                                            <div className="input-group mb-2 mr-sm-2">
                                                <div className="input-group-prepend">

                                                    <div className="input-group-text">Search</div>
                                                </div>
                                                <input onChange={e => this.handleSearchChange(e.target.value)} type="text" className="form-control" id="inlineFormInputGroupUsername2" placeholder="Student First or Last Name" />
                                            </div>
                                        </form>



                                    </div>
                                    <div className="row">
                                        <div className="col-md-3 border-right overflow-auto mt-4">
                                            <h5>Courses</h5>
                                            <div className="btn-group-vertical">

                                                {/* List of all courses that have been searched for */}

                                                {courseItems}


                                            </div>
                                        </div>

                                        <div className="col mt-4">

                                            <div className="col-md-12 pl-3">


                                                <Bounce top>
                                                    {/* Form to create a new course */}

                                                    <div id="assignStudents">
                                                        <AssignStudentForm course={this.state.selectedCourse} selectedStudents={this.state.studentList} />
                                                    </div>



                                                </Bounce>
                                            </div>
                                        </div>
                                        <div className="col-md-3 border-left overflow-auto mt-4">
                                            <h5>Students</h5>
                                            <fieldset className="form-group">

                                                {/* Student List Goes Here */}

                                            </fieldset>
                                        </div>
                                    </div>
                                </div>

                            </div>
                            <div className="modal-footer">
                                <button onClick={this.handleSubmit} className="btn btn-primary mr-auto">Save Student Assignments</button>
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
     
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    courses: state.courses,
    students: state.students
})

export default connect(mapStateToProps, {})(AssignStudentModal);
