import React, { Component, useState } from 'react';
import {
    BrowserRouter as Router,
    NavLink,
    Switch,
    Route,
    Link,
    Redirect,
    withRouter
} from "react-router-dom";
import LoginPage from '../../pages/login/LoginPage';
import axios from 'axios';
import FadeIn from 'react-fade-in';
import Bounce from 'react-reveal/Bounce'



class LoginBox extends Component {
    constructor(props) {
        super()
        this.handleSubmitError = this.handleSubmitError.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            email: "",
            password: "",
            message: ""
        }
    }

    setEmail(value) {
        this.setState({
            email: value
        })
    }

    setPassword(value) {
        this.setState({
            password: value
        })
    }

    route(path) {

    }

    handleSubmitError(text) {
        console.log("IN")
        this.setState({
            message: text
        })
        document.getElementById("error-text").style = { display: "block" }
    }


    handleSubmit(event) {
        console.log("SUBMIT")
        event.preventDefault()
        //console.log(this.state.email)
        if (this.state.email.length > 0 && this.state.password.length > 0) {
            const user = {
                "email": this.state.email,
                "password": this.state.password
            } 

            
            

            if ((this.props.location.path != "/login-student") ||
                (this.props.location.path != "/login-parent") ||
                (this.props.location.path != "login-staff")) {

                this.handleSubmitError("Please Select a User Type")
            } else (
                axios.post("http://localhost:8080/login", user)
                    .then((response) => {

                        if (response.data != "") {

                            sessionStorage.setItem("user", response.data.email);
                            switch (response.data.type) {
                                case "TEACHER":
                                    this.props.history.push('/teacher')
                                    break;
                                case "STUDENT":
                                    this.props.history.push('/student')
                                    break;
                                case "PARENT":
                                    //this.props.history.push('/admin')
                                    break;
                                case "ADMIN":
                                    this.props.history.push('/admin')
                                    break;
                                default:
                                    break;
                            }

                        } else {

                            sessionStorage.clear()
                            this.handleSubmitError("Username or password is incorrect")
                        }
                    }).catch()
            )
        } else {
            this.handleSubmitError("Username or Password cannot be blank")
        }
    }

    render() {
        console.log(this.props.location.pathname)
        return (
            <FadeIn transitionDuration={1000}>
                <div className="mt-5 mx-auto w-50 p-3">
                    <div className="rounded-lg shadow p-3 bg-light">

                        <ul className="nav nav-tabs">
                            <li className="nav-item">
                                <NavLink activeClassName="active" className="nav-link" to="student/login-student">Student</NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink activeClassName="active" className="nav-link" to="parent/login-parent">Parent</NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink activeClassName="active" className="nav-link" to="staff/login-staff">Staff</NavLink>
                            </li>
                        </ul>

                        <Bounce>
                            <div id="error-text" style={{ display: "none" }}>
                                <small className="text-danger">{this.state.message}</small>
                            </div>
                        </Bounce>
                        <div>
                            <form onSubmit={this.handleSubmit}>

                                <div class="form-group mt-4">
                                    <label for="exampleInputEmail1">Email address</label>

                                    <input
                                        type="email"
                                        className="border-top-0 border-left-0 border-right-0 rounded-0 form-control"
                                        value={this.state.email}
                                        autoFocus
                                        onChange={e => this.setEmail(e.target.value)}
                                        id="email"
                                        aria-describedby="Email Address" />
                                    <small id="emailHelp" className="form-text text-muted"></small>
                                </div>
                                <div class="form-group">
                                    <label for="exampleInputPassword1">Password</label>
                                    <input
                                        type="password"
                                        className="border-top-0 border-left-0 border-right-0 rounded-0 form-control"
                                        value={this.state.password}
                                        onChange={e => this.setPassword(e.target.value)}
                                        id="password" />
                                </div>
                                <div className="form-group">
                                    <button type="submit" class="btn btn-primary w-100">Submit</button>
                                </div>
                                <a href="#" className="text-muted"><small>Register</small></a><a className="float-right text-muted font-size" href="#"><small>Need help?</small></a>

                            </form>
                        </div>

                    </div>
                </div>
            </FadeIn>




        );
    }
}

export default withRouter(LoginBox);
