import ReactDataSheet from 'react-datasheet';
import React from 'react';
// Be sure to include styles at some point, probably during your bootstrapping
import 'react-datasheet/lib/react-datasheet.css';

class GradeEntryTable extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        grid: [
          [{ value: 1, width: 100}, { value: 3, width: 100 }],
          [{ value: 2 }, { value: 4 }],
        ],
      };
    }
    render() {
        const tableHead = []
        for (let i = 0; i < 15; i++) {
            tableHead.push(<th>Assignment {i}</th>)
        }
      return (
        <ReactDataSheet  
            sheetRenderer={props => {
                return (
                    <table className="table table-hover table-sm table-striped table-bordered">
                        <thead>
                            <tr>
                                {tableHead}
                            </tr>
                        </thead>
                        <tbody>
                            {props.children}
                        </tbody>
                    </table>
                )
            }}

            dataEditor={props => {
                return (
                    <input type="text" className="p-0" style={inputStyle} placeholder={props.cell.value}></input>
                )
            }}

          data={this.state.grid}
          valueRenderer={cell => cell.value}
          onCellsChanged={changes => {
            const grid = this.state.grid.map(row => [...row]);
            changes.forEach(({ cell, row, col, value }) => {
              grid[row][col] = { ...grid[row][col], value };
            });
            this.setState({ grid });
          }}
        />
      );
    }
  }
  
  export default GradeEntryTable
