import React, { Component } from 'react';

class GradeEntryTable extends Component {
    render() {
        return (
            <div>
                <div className="table-responsive">
                <table className="table">
                  <thead>
                    <tr>
                      <th scope="col">#</th>
                      <th scope="col">First</th>
                      <th scope="col">Last</th>
                      <th scope="col">Handle</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <th scope="row">1</th>
                      <td><input type="text" className="form-control mb-2 mr-sm-2" placeholder="Jane Doe" /></td>
                      <td><input type="text" className="form-control mb-2 mr-sm-2" placeholder="Jane Doe" /></td>
                      <td>@<input type="text" className="form-control mb-2 mr-sm-2" placeholder="Jane Doe" /></td>
                    </tr>
                    <tr>
                      <th scope="row">2</th>
                      <td>Jacob</td>
                      <td>Thornton</td>
                      <td>@fat</td>
                    </tr>
                  </tbody>
                </table>
                </div>
                
            </div>
        );
    }
}

export default GradeEntryTable;




