import React, { Component } from 'react';
import ButtonGroup from './ButtonGroup';

class MessagePreview extends Component {

    length(text) {
        const MAX_LENGTH = 30;
        let message = text;

        if (message.length > MAX_LENGTH) {
            message = message.slice(0, MAX_LENGTH) + "...";
        }

        return message
    }


    render() {
        
        return (
            <tr>
                <th scope="row">1</th>
                <td className="text-small text-nowrap">{this.length(this.props.message.name)}</td>
                <td className="text-small text-nowrap">{this.props.message.email}</td>
                <td className="text-small text-nowrap">{this.props.message.subject}</td>
                <td className="text-small text-nowrap">{length}</td>
                <ButtonGroup />
            </tr>
        );
    }
}

export default MessagePreview;
