import React, { Component } from 'react';
import { connect } from 'react-redux'
import Trash from './../../images/icons/trash.svg'
import { holdTempAssignment } from './../../action/tempActions'
import Bounce from 'react-reveal/Bounce'

class CreateAssignmentTable extends Component {
    constructor() {
        super()
        this.editAssignment = this.editAssignment.bind(this)
        this.handleUpdate = this.handleUpdate.bind(this)
        this.state = {
            edit: false
        }
    }

    handleUpdate() {
        this.setState({
            edit: !this.state.edit
        })

        if(this.state.edit) {
            document.getElementById("update-button").style.display = 'block';
        } else {
            document.getElementById("update-button").style.display = 'none';
        }
    }

    handleClick(assignment) {
        this.props.holdTempAssignment(assignment)
    }

    editAssignment(id) {
        if(this.state.edit) {
            if(this.props.this.props.editAssignment.id === id) {
                return true
            } else {
                return false
            }
        } else {
            return false
        }
    }

    render() {
        let count = 0;
        //get course that is selected and find all assignments for that course
        const assignmentList = this.props.editCourse && this.props.editCourse.assignments && this.props.editCourse.assignments.map(assignment => {
            count++;
            return (
                <tr key={assignment.id} 
                className={this.props.editAssignment && this.props.editAssignment.id === assignment.id ? "bg-warning" : null} 
                onClick={() => this.handleClick(assignment)}>
                    
                    <th scope="row">{count}</th>
                    <td>{this.editAssignment(assignment.id) ? <input className="form-control" type="text" placeholder={assignment.name}></input>  : assignment.name}</td>
                    <td>{this.editAssignment(assignment.id) ? <input className="form-control" type="text" placeholder={assignment.totalPoints}></input>  : assignment.totalPoints}</td>
                    <td>{this.editAssignment(assignment.id) ? <input className="form-control" type="date" placeholder={assignment.dueDate}></input>  : assignment.dueDate}</td>
                    <td>{this.editAssignment(assignment.id) ? <input className="form-control" type="text" placeholder={assignment.assignmentType}></input>  : assignment.assignmentType}</td>
                    {/* <td><button className="btn btn-danger"><img className="p-0" style={{width: '12px'}} src={Trash}/></button></td> */}
                    
                </tr>
            )
        })
        console.log(this.state.edit)
        
        return (
            <div>
            <div className="row bg-light my-1">
                <button className="btn btn-danger mx-2">Delete Selected</button>
                <button className="btn btn-secondary mx-2" onClick={this.handleUpdate}>Edit Selected</button>
                <Bounce><button className="btn btn-primary ml-auto mr-2"
                    style={{display: 'none'}} id="update-button">Update</button></Bounce>
            </div>
            <table class="table table-hover table-sm">
                <thead>
                    <tr>
                        <th scope="col"></th>
                        <th scope="col">Name</th>
                        <th scope="col">Points</th>
                        <th scope="col">Due Date</th>
                        <th scope="col">Type</th>
                    </tr>
                </thead>
                <tbody>
                    {assignmentList}
                </tbody>
            </table>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    editCourse: state.temp.editCourse,
    editAssignment: state.temp.editAssignment
})

export default connect(mapStateToProps, { holdTempAssignment })(CreateAssignmentTable);
