import React, { Component } from 'react';
import Header from '../../components/main_page/header/Header';
import './admin_page.css';
import UserHomeLayout from '../../layout/UserHomeLayout';
import axios from 'axios';

class AdminPage extends Component {
    constructor() {
        super()
        this.loadAllUsers = this.loadAllUsers.bind(this)
        this.state = {
            courseList: [],
            studentList: [],
            parentList: [],
            teacherList: [],
            user: "",
            message: ""
        }
    }

    loadAllUsers() {

    }

    updateStudentList(tempStudentList) {
        this.setState({
            studentLst: tempStudentList
        })
    }

    updateCourseList(tempCourseList) {
        this.setState({
            courseList: tempCourseList
        })
    }

    updateParentList(tempUserList) {
        this.setState({
            parentList: tempUserList
        })
    }

    componentWillMount() {
        const email = sessionStorage.getItem("user")
        const user = {
            email: email
        }

        axios.post("http://localhost:8080/all", user).then((response) => {
            console.log(response.data)
            if (response.data != "") {
                this.setState({
                    user: response.data
                })
            } else {
                sessionStorage.clear()
                this.setState({
                    message: "Error retrieving data, please logout and try again"
                })
            }
        }
        )

        
    }

    componentDidMount() {

        axios.get("http://localhost:8080/course/all").then(response => {
            if (Array.isArray(response.data)) {
                this.setState({
                    courseList: response.data
                })
            }
        })

        axios.get("http://localhost:8080/all").then(response => {
            if (Array.isArray(response.data)) {
                this.setState({
                    userList: response.data
                })
            }
            
        })


    }

    render() {
        return (
            <div className="">
                <Header
                courseList={this.state.courseList}
                updateCourseList={(courseList)=> this.updateCourseList(courseList)} 
                updateUserList={(userList)=> this.updateUserList(userList)} 
                userList={this.state.userList} 
                user={this.state.user} />
                {this.state.user ? <UserHomeLayout userList={this.state.userList} user={this.state.user} /> : <h3>{this.state.message}</h3>}
            </div>
        );
    }
}


export default AdminPage;
