import { GET_USER, HOLD_EDIT_USER, UPDATE_USER, UPDATE_MESSAGES } from '../action/types'
import { GET_ALL_USERS, ADD_CALENDAR_ITEM, REMOVE_CALENDAR_ITEM } from './../action/types';

const initialState = {
    user: {}
}

export default function(state = initialState, action) {
    switch (action.type) {
        case REMOVE_CALENDAR_ITEM:
            let otherTempUser = { ...state.user }
            const index = otherTempUser.user.calendarList.filter(item => {
                return action.payload === item.id
            })
            otherTempUser.calendarList.splice(index, 1)

            return {
                ...state,
                user: otherTempUser
            }
        case ADD_CALENDAR_ITEM:
            let tempUser = { ...state.user }

            tempUser.user.calendarList.push(action.payload)

            return {
                ...state,
                user: tempUser
            }
        case GET_USER:
            return {
                ...state,
                user: action.payload
            }
        case UPDATE_MESSAGES:
            return {
                ...state,
                messages: action.payload
            };
        case GET_ALL_USERS:
            return {
                ...state,
                users: action.payload
            }
        default:
            return state;
    }
}
