import React, { Component } from 'react';
import Menu from '../../../images/icons/menu.svg';
import Message from '../../../images/icons/message.svg';
import Calendar from '../../../images/icons/calendar.svg';
import Alert from '../../../images/icons/alert.svg';


{/*Requires a userName, dropDownMenuItem list, picture */}
class Header extends Component {



    render() {
        const dropLeft = {
            right: "0",
            left: "auto",
            width: "200px"
        }
        return (
            <div>
                <nav className="navbar navbar-light bg-light p-0 row position-sticky">
                    <div className="col-md-2 bg-secondary w-100 text-center py-0">                
                            <div className="row p-1">
                                {/* Image below needs to be customized to the user */}
                            <div className="col-sm-4 my-auto p-0"><img src={this.props.userImage} width="30" height="30" className="rounded-circle" /></div>
                            <div className="col p-0"><span className="navbar-brand mb-0 display-4 text-light mx-auto">{this.props.userName}</span></div>
                            </div>
                    </div>
                    <div className="col-md-10 pr-5">
                        
                        <div className="float-right postion-relative">
                            <a className="nav-link dropdown pl-3 p-0 m-0" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false"><img src={Menu} width="30" height="30"/></a>
                            <div className="position-relative">
                            <div style={dropLeft} className="dropdown-menu">
                                {this.props.dropDownMenuItems}
                                {/*
                                List of items to be populated based on user type
                                */}
                                <a className="dropdown-item" href="#">Action</a>
                                <a className="dropdown-item" href="#">Another action</a>
                                <a className="dropdown-item" href="#">Something else here</a>
                                <div className="dropdown-divider"></div>
                                <a className="dropdown-item" href="#">Separated link</a>
                            </div>
                            </div>
                        </div>
                        <div className="float-right">
                            <a className="" href="#"><span class="position-absolute badge badge-light">4</span><img className="px-3" src={Message}></img></a>
                        </div>
                        <div className="float-right">
                            <a className="" href="#"><img className="px-3" src={Calendar}></img></a>
                        </div>
                        <div className="float-right">
                            {/* ------------------- Use opacity to display or not ------------------------ */}
                            <a className="" href="#"><img style={{opacity: 1}} className="px-3" src={Alert}></img></a> 
                        </div>

                    </div>

                </nav>

            </div>
        );
    }
}

export default Header;
