import axios from 'axios'
import { GET_ALL_COURSES, ADD_COURSE } from './types';

export const getAllCourses =()=> dispatch => {
    axios.get("http://localhost:8080/course/all")
    .then(response => {
        dispatch({
            type: GET_ALL_COURSES,
            payload: response.data
        })
    })
}

export const addCourse =()=> dispatch => {
    axios.get("http://localhost:8080/course/new")
    .then(response => {
        dispatch({
            type: ADD_COURSE,
            payload: response.data
        })
    })
}
