import React, { Component } from 'react';
import Checkmark from '../../../images/icons/checkmark.svg';
import Trash from '../../../images/icons/trash.svg';

class ToDoItem extends Component {
  constructor() {
    super()

    this.handleClick = this.handleClick.bind(this)

    this.state = {
      complete: false
    }
  }

  handleClick() {
    console.log(this.state.complete)

    this.setState((prevState) => {
      return {
        complete: !prevState.complete
      }
    })
    this.forceUpdate()
  }

  render() {
      //All events need to be functions
      let completeStyle = {
        transitionDuration: '1s'
      }
      
      if (this.state.complete) {
        completeStyle = {
          opacity: .4,
          textDecoration: "line-through",
          transitionDuration: '1s'
        }
      }

        return (
                <div className="card p-3 col-md-2 m-3 rounded shadow" style={completeStyle}>
                  <blockquote className="blockquote mb-0 card-body" style={completeStyle}>
                    {this.props.text}
                  </blockquote>
                  <div className="row">
                    <button onClick={this.handleClick} className="btn btn-primary"><span style={completeStyle}><img src={Checkmark}/></span></button>
                    <button onClick={() => this.props.remove(this.props.text)} className="btn btn-info"><img src={Trash}/></button>
                  </div>
                </div>
        );
    }
}

export default ToDoItem;
