import React, { Component } from 'react';
import axios from 'axios'
import CourseItem from './CourseItem';

class AddCourseForm extends Component {
    constructor() {
        super()
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            teacher: "",
            title: ""
        }
    }

    setTitle(value) {
        this.setState({
            title: value
        })
    }

    setTeacher(value) {
        this.setState({
            teacher: value
        })
        console.log(this.state.teacher)
    }

    handleSubmit (event) {
        event.preventDefault();
        
        axios.post(htt)


        const course = {
            name: this.state.title,
            teacher: {
                id: this.state.teacher ? this.state.teacher : document.getElementById("teacher-id").value
            }
        }
        console.log(course)
        this.props.handleSubmit(course)
        document.getElementById("form-to-clear").reset();
    }

    componentDidMount() {
        
        
    }

    render() {
            const teacherList = this.props.userList.map(user => {
            if (user.type === "TEACHER") {
                return <option value={user.id}>{user.firstName} {user.lastName}</option>
            }
    
        })
        return (
            <div>
                <form id="form-to-clear" onSubmit={this.handleSubmit}>
                    <div className="form-row w-100">
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="title">Course Title</label>
                            <input onChange={e => this.setTitle(e.target.value)} type="text" name="title" className="form-control w-100" id="email" aria-describedby="emailHelp" placeholder="Course Title" required />
                 
                        </div>
                        <div className="form-group col-md-6 w-100">
                        <label htmlFor="teacher">Teacher</label>
                            <select className="custom-select mr-sm-2" id="teacher-id" onChange={e => this.setTeacher(e.target.value)}name="teacher" required>
                              {teacherList}
                            </select>
            
                        </div>
                    </div>
                    <div className="form-row w-100">
                        <button type="submit" className="btn btn-outline-primary mx-auto w-75">Create New Course</button>
                    </div>
                </form>
            </div>
        );
    }
}

export default AddCourseForm;
