import React, { Component } from 'react';
import './gradeEntry.css';
import TableItemHead from './TableItemHead';
import TableItem from './TableItem';


class GradeEntryTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
          grid: [
            [{ value: 1 }, { value: 3 }],
            [{ value: 2 }, { value: 4 }],
          ],
        };
      }
    render() {
        const asList = [
                {
            title: "Introduction to Class",
            points: 15
        },
        {
            title: "Assignment 1",
            points: 20
        }
        ]



        const assignmentList = asList.map(item => {

            return <TableItemHead title={item.title} points={item.points}/>
        })
        assignmentList.unshift(<TableItemHead title="Student Name"/>)

        

        return (
            class App extends React.Component {
                
                render() {
                  return (
                    <ReactDataSheet
                      data={this.state.grid}
                      valueRenderer={cell => cell.value}
                      onCellsChanged={changes => {
                        const grid = this.state.grid.map(row => [...row]);
                        changes.forEach(({ cell, row, col, value }) => {
                          grid[row][col] = { ...grid[row][col], value };
                        });
                        this.setState({ grid });
                      }}
                    />
                  );
                }
              }
            
        );
    }
}

export default GradeEntryTable;
