import axios from 'axios'

const getUser = (type) => {
    axios.get(`http://localhost:8080/user/find/${sessionStorage.getItem("user")}`).then(response => {
        return response.data
    })
}

const userReducer = (state = getUser(type), action) => {
    switch (action.type) {
        case 'SET_USER':
            return action.payload
        case 'GET':
            return state;
        case 'REMOVE':
            return state;
    }
}

export default userReducer;
