import axios from 'axios';
//Reducer is a switch statement with actions and defines those actions

//state is the initial state 
const getStudentList = () => {
    return new Promise((resolve, reject) => {
        axios.get("http://localhost:8080/student/all").then(response => {
            response => resolve(response.data)
        }).catch(error => reject(error))
        response ? resolve(response) : reject('ERROR')
    });
}

//Is it better to return the list from the API call and set state to that, or update state on my own
const studentReducer = (state = getStudentList(), action) => {
    switch (action.type) {
        case 'ADD':
            if(Array.isArray(state)) {
                state.push(action.payload)
                axios.post("http://localhost:8080/student/new", action.payload).then(response => {
                    //check to see what response means executed ok and put in some if logic there
                })
            }
            return state;
        case 'REMOVE':
            if(Array.isArray(state)) {
                state.push(action.payload)
            }
            return null;
        case 'UPDATE':
            return null;
        case "ALL":
            return state;
        default:
            break;
    }
} 

export default studentReducer;
