import axios from 'axios'
import { GET_USER } from '../../action/types'

const initialState = {
    user: {}
}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_USER:
            return action.payload
        case 'REMOVE':
            return state;
        default:
            return state;
    }
}

export default userReducer;
