import React, { Component } from 'react';
import axios from 'axios'
import saveUser from './../../databaseUtil/databaseUtil';

class AddUserForm extends Component {
    constructor() {
        super()
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            states: [],
            dob: "",
            firstName: "",
            lastName: "",
            email: "",
            password: "",
            address: "",
            unit: "",
            city: "",
            state: "",
            zipcode: "",
            type: "".toUpperCase(),
            tempUser: ""
        }
    }

    setDob(value) {
        this.setState({
            dob: value
        })
    }

    setEmail(value) {
        this.setState({
            email: value
        })
    }

    setPassword(value) {
        this.setState({
            password: value
        })
    }

    setFirstName(value) {
        this.setState({
            firstName: value
        })
    }

    setLastName(value) {
        this.setState({
            lastName: value
        })
    }

    setAddress(value) {
        this.setState({
            address: value

        })
    }

    setUnit(value) {
        this.setState({

            unit: value

        })
    }

    setCity(value) {
        this.setState({

            city: value

        })
    }

    setUserState(value) {
        this.setState({

            state: value

        })
    }

    setZipcode(value) {
        this.setState({

            zipcode: value

        })
    }

    setType(value) {

        this.setState({
            type: value
        })
    }

    handleSubmit(event) {
        event.preventDefault();
    
        const user = {
          firstName: this.state.firstName,
          lastName: this.state.lastName,
          dob: this.state.dob,
          email: this.state.email,
          password: this.state.password,
          type: this.state.type ? this.state.type : document.getElementById("type").value,
          contactInformation: [{
            address: this.state.address,
            city: this.state.city,
            state: this.state.state,
            zipcode: this.state.zipcode
          }]
        }

        const tempUser = {
            id: Math.floor(Math.random() * 1000),
            firstName: user.firstName,
            lastName: user.lastName
        }
        console.log(this.state.tempUser)
        saveUser(user)
        
        document.getElementById("form-to-clear").reset();
    
        this.setState({
            tempUser: tempUser,
            success: "Success!"
        }
        )

        

      }

    componentDidMount() {
        axios.get("http://localhost:8080/getStates").then(response => {
            const temp = response.data.map(st => {
                return <option>{st}</option>
            })

            this.setState({
                states: temp
            })
        })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit} id="form-to-clear">
                    <div className="form-row w-100">
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="email">Email address</label>
                            <input onChange={e => this.setEmail(e.target.value)} type="email" name="email" className="form-control w-100" id="email" aria-describedby="emailHelp" placeholder="Enter email" required />
                            <small id="emailHelp" className="form-text text-muted">
                                We'll never share your email with anyone else.
                                      </small>
                        </div>
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="password">Password</label>
                            <input onChange={e => this.setPassword(e.target.value)} type="password" name="password" className="form-control w-100" id="password" placeholder="Password" required />
                            <small id="emailHelp" className="form-text text-muted">
                                We'll never share your email with anyone else.
                                      </small>
                        </div>
                    </div>

                    <div className="form-row w-100">

                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="password">First Name</label>
                            <input required onChange={e => this.setFirstName(e.target.value)} type="text" name="firstName" className="form-control w-100" id="firstName" aria-describedby="emailHelp" placeholder="First Name" />
                        </div>

                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="password">Last Name</label>
                            <input required onChange={e => this.setLastName(e.target.value)} type="text" name="lastName" className="form-control w-100" id="lastName" placeholder="Last Name" />
                        </div>
                    </div>
                    <div className="form-group">
                        <label for="inputAddress">Address</label>
                        <input required onChange={e => this.setAddress(e.target.value)} type="text" className="form-control" id="inputAddress" name="address" placeholder="1234 Main St" />
                    </div>
                    <div className="form-row">
                        <div className="form-group col">
                            <label for="inputAddress2">Address 2</label>
                            <input onChange={e => this.setUnit(e.target.value)} type="text" className="form-control" id="inputAddress2" name="unit" placeholder="Apartment, studio, or floor" />
                        </div>
                        <div className="form-group col">
                            <label for="inputAddress2">Date of Birth</label>
                            <input required onChange={e => this.setDob(e.target.value)} type="date" className="form-control" id="inputAddress2" name="unit" placeholder="Apartment, studio, or floor" />

                        </div>
                    </div>
                    <div className="form-row">
                        <div className="form-group col-md-6">
                            <label for="inputCity">City</label>
                            <input required onChange={e => this.setCity(e.target.value)} type="text" className="form-control" name="city" id="inputCity" />
                        </div>
                        <div className="form-group col-md-4">
                            <label for="inputState">State</label>
                            <select required onChange={e => this.setUserState(e.target.value)} id="inputState" name="state" className="form-control">
                                {this.state.states}
                            </select>
                        </div>
                        <div className="form-group col-md-2">
                            <label for="inputZip">Zip</label>
                            <input onChange={e => this.setZipcode(e.target.value)} type="text" name="zipcode" className="form-control" id="inputZip" />
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="input-group col">
                            <p className="text-alert">{this.state.message}</p>
                            <select id="type" onChange={e => this.setType(e.target.value)} className="custom-select mt-4" name="type" required>
                                <option value="STUDENT" selected>Student</option>
                                <option value="ADMIN">Admin</option>
                                <option value="PARENT">Parent</option>
                                <option value="TEACHER">Teacher</option>
                            </select>
                        </div>
                        <div className="col">

                            <input type="submit" onClick={()=> this.props.updateUserList(this.state.tempUser)} className="btn btn-primary w-100 mt-4">

                            </input>

                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default AddUserForm;
