import { GET_USER, HOLD_EDIT_USER, UPDATE_USER } from '../action/types'

const initialState = {
    user: {}
}

export default function(state = initialState, action) {
    switch (action.type) {
        case UPDATE_USER:

            let tempData = {
                editUser: state.temp && state.temp.editUser,
                editUserType: state.temp && state.temp.editUserType
            }

            tempData.editUser.user = action.payload
            // let tempUser = state.temp.editUser
            // tempUser.user = action.payload
            // console.log(tempNew)
            return {
                ...state,
                temp: tempData
            };
        case GET_USER:
            return {
                ...state,
                user: action.payload
            }
        case 'REMOVE':
            return state;
        default:
            return state;
    }
}
