import React, { Component } from 'react';
import { connect } from 'react-redux'

class GradeEntryClassNavBar extends Component {
    
    selected(e) {
        document.getElementById(e.target.id).selected = true
    }
    
    render() {

        const courses = this.props.courseList && this.props.courseList.map(course => {
            console.log(course)
            return (
                <li key={course.id} id={course.id} className="nav-item" onClick={className="nav-item active"}>
                    <a className="nav-link">{course.name}</a>
                </li>
            )
        })
        //Code to return a navbar with a list of all the teachers classes
        return (
            <div className="pt-2">
                <ul class="nav nav-pills">
                    {courses}
                </ul>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    user: state.user.user,
    courseList: state.user.user.courseList
})

export default connect(mapStateToProps, {})(GradeEntryClassNavBar);
