import React, { Component } from 'react';
import { connect } from 'react-redux'
import { holdTempCourse } from './../../../../action/tempActions';

class CourseItem extends Component {
    constructor() {
        super()
        this.handleSelect = this.handleSelect.bind(this)
        this.state = {

        }
    }

    handleSelect(e) {
        this.props.selectCourse()
        this.props.holdTempCourse(e.target.value)
    }

    render() {
        return (
            <button
            className="btn btn-secondary" type="button"
            onClick={this.handleSelect} 
            name="course-button" id="course-button" value={this.props.course}>
                {this.props.course.name}
            </button>
        );
    }
}

const mapStateToProps = state => ({
    temp: state.temp
})

export default connect(mapStateToProps, { holdTempCourse })(CourseItem);
