        this.props.user.contactInformation &&  this.props.user.contactInformation.address : <a href="#addressModal">Add an Address </a>;
        
        const picture = this.props.user && this.props.user.picture ? this.props.user.picture : DefaultImage;
        phoneList = phoneList.length != 0 ? phoneList : <a href="#">Add a phone number</a>; 

  
        return (
            <div>
                <div className="row p-4">
                    <div className="col-md-4 border border-left pt-4 pb-2">
                        <div className="row text-center pb-3">
                        <a href="#" className="mx-auto"><img src={picture} width="100" height="100" className="border rounded-circle mx-auto" /></a>
                        </div>
                        <div className="row">
                            <div className="col">
                                <h6 className="text-muted text-center">
                                    {this.props.user && this.props.user.firstName} {this.props.user && this.props.user.lastName}</h6>
                                <hr></hr>
                                <a href="#" ><p className="text-muted text-center">{address}</p></a>
                                <p className="text-muted text-center">{this.props.user && this.props.user.email}</p>
                                {phoneList}
                            </div>
                        </div>
                    </div>
                    <div className="col-md-8">

                    </div>

                </div>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    user: state.user.user.user
})

export default connect(mapStateToProps, {})(UserInformation);
