import React, { Component } from 'react';

class EditCourseForm extends Component {
    constructor() {
        super()
        this.state = {
            startDate: "",
            endDate: "",
            startTime: "",
            roomNumber: ""
        }
    }

    setStartDate(value) {
        this.setState({
            startDate: value,

        })
    }

    setEndDate(value) {
        this.setState({
            endDate: value
        })
    }
    
    setStartTime(value) {
        this.setState({
            startTime: value
        })
    }
    
    setRoomNumber(value) {
        this.setState({
            roomNumber: value
        })
    }


    render() {
        let date = "";

        // if (this.props.user.dob) {
        //     let temp = new Date(Date.parse(this.props.user.dob))
        //     date = temp.toISOString()
        // }
        return (
            <div>
                <form>
                    <div className="row">
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Course Start Date</label>
                                <input
                                    onChange={e => this.setStartDate(e.target.value)}
                                    value={date ? date.substr(0, 10) : null}
                                    type="date" className="form-control"
                                
                                    id="inputAddress2"
                                    name="dob"
                                    placeholder={date ? date.substr(0, 10) : ""} />

                            </div>
                        </div>
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Course End Date</label>
                                <input
                                    onChange={e => this.setEndDate(e.target.value)}
                                    value={date ? date.substr(0, 10) : null}
                                    type="date" className="form-control"
                                    //onfocus={e=> this.setDob(e.target.selected)}
                                    id="inputAddress2"
                                    name="dob"
                                    placeholder={date ? date.substr(0, 10) : ""} />

                            </div>
                        </div>
                        
                    </div>
                    <div className="row">
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Start Time</label>
                                <input
                                    onChange={e => this.setStartTime(e.target.value)}
                                    value={this.state.startTime}
                                    type="text" className="form-control"
                                    //onfocus={e=> this.setDob(e.target.selected)}
                                    id="inputAddress2"
                                    name="dob"
                                    placeholder={this.state.startTime} />

                            </div>
                        </div>
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Days of Week Course Meets</label>
                                <input
                                    onChange={e => this.setCourseMeeting(e.target.value)}
                                    value={this.state.courseMeeting}
                                    type="text" className="form-control"
                                    
                                    id="inputAddress2"
                                    name="courseMeeting"
                                    placeholder={this.state.courseMeeting} />

                            </div>
                        </div>

                        

                    </div>
                </form>
            </div>
        );
    }
}

export default EditCourseForm;
