import { axios } from 'axios';
import { GET_ALL_TEACHERS, REMOVE, ADD, UPDATE } from '../../action/types';
//Reducer is a switch statement with actions and defines those actions

const initialState = {}

//Is it better to return the list from the API call and set state to that, or update state on my own
const teacherReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_ALL_TEACHERS:
            return {
                ...state,
                teachers: action.payload
            };
        case REMOVE:
            return {
                ...state,
                teacher: action.paylod
            }
        case UPDATE:
            return null;
        case ADD:
            return state;
        default:
            return state;
    }
} 

export default teacherReducer;
