import React, { Component } from 'react';
import { connect } from 'react-redux'
import { addUserToList, removeUserFromList } from './../../../action/tempActions';

class RecipientItem extends Component {
    constructor() {
        super()
        this.handleAddRecipient = this.handleAddRecipient.bind(this)
        this.state = {
        }
    }

    handleAddRecipient(currentUser) {
        //Use the temp form
        if (!this.state.checked) {
            this.props.addUsertoList(currentUser)
            //this.props.addStudentToCourse(currentUser)
        } else {
            this.props.removeUserFromList(currentUser.id)
            //this.props.removeStudentFromCourse(currentUser.id)
        }
        this.setState({
            checked: !this.state.checked
        })

    }

    componentDidUpdate(prevProps) {
        if(prevProps.editCourse === undefined) {
            if (this.props.editCourse.students && this.props.editCourse.students.find((student) => student.id === this.props.currentStudent.id)) {
                this.setState({
                    checked: true
                })
            } else {
                this.setState({
                    checked: false
                })
            }
        } else if (this.props.editCourse && this.props.editCourse !== prevProps.editCourse && prevProps.editCourse) {

            if (this.props.editCourse.students && this.props.editCourse.students.find((student) => student.id === this.props.currentStudent.id)) {
                console.log(this.props.currentStudent.id)
                this.setState({
                    checked: true
                })
            } else {
                this.setState({
                    checked: false
                })
            }
        }

    }
    render() {
        return (
        <button type="button" id={this.props.id} className={this.state.checked ? "btn btn-primary rounded-pill text-nowrap" :
        "btn btn-seconday rounded-pill text-nowrap"}
        onClick={() => this.handleAddRecipient(this.props.currentUser)}>
            {this.props.firstName} {this.props.lastName}
        </button>

        );
    }
}

export default connect(mapStateToProps)(RecipientItem);
