import React, { Component } from 'react';
import ButtonGroup from './ButtonGroup';

class MessagePreview extends Component {

    length(text) {
        const MAX_LENGTH = 30;
        let message = text;

        if (message.length > MAX_LENGTH) {
            message = message.slice(0, MAX_LENGTH) + "...";
        }

        return message
    }


    render() {
        
        return (
            <tr>
                <th scope="row"></th>
                <td className="text-small text-nowrap">{this.length(this.props.message.name)}</td>
                <td className="text-small text-nowrap">{this.length(this.props.message.email)}</td>
                <td className="text-small text-nowrap">{this.length(this.props.message.subject)}</td>
                <td className="text-small text-nowrap">{this.length(this.props.message.innerText)}</td>
                <td>
                <div className="row w-100">
                <button className="btn">
                    <img width="25" height="25" src={Read}/>
                </button>
                <button className="btn">
                    <img width="25" height="25" src={Replay}/>
                </button>
                <button className="btn">
                    <img width="25" height="25" src={Delete}/>
                </button>
                <button className="btn">
                    <img width="25" height="25" src={Tag}/>
                </button>
                

                </div>
            </td>
         
            </tr>
        );
    }
}

export default MessagePreview;
