import React, { Component } from 'react';
import { HotTable } from '@handsontable/react'
import 'handsontable/dist/handsontable.full.css';
import 'handsontable/dist/handsontable.full.css'
import { connect } from 'react-redux';
import { gridHandleChange } from './../../../action/tempActions'

class Table extends Component {
    constructor() {
        super()
        this.data = [
        ]
    }

    handleChange(change, source) {

        // if (source === 'loadData') {
        //     return; //don't save this change
        //   }
        //   if (!autosave.checked) {
        //     return;
        //   }

        //   clearTimeout(autosaveNotification);
        //   ajax('scripts/json/save.json', 'GET', JSON.stringify({data: change}), function (data) {
        //     exampleConsole.innerText  = 'Autosaved (' + change.length + ' ' + 'cell' + (change.length > 1 ? 's' : '') + ')';
        //     autosaveNotification = setTimeout(function() {
        //       exampleConsole.innerText ='Changes will be autosaved';
        //     }, 1000);
        //   });
        

        this.props.gridHandleChange()
    }

    componentDidUpdate() {

    }

    colHeaders(index) {
        return index + ': ID'
    }

    render() {
        
        let colHeaders = []
        let columnSchema = []
        let rowHeaders = []
        let data = this.props.data ? this.props.data : []

        if (this.props.editCourse && this.props.editCourse.students) {
            rowHeaders = this.props.editCourse.students.map(student => {
                
                //Creates an object that will be used to organize the data
                let studentArray = []
                student.assignmentScores.forEach(score => {
                    studentArray.push([score.assignmentTempId, score.points])
                });
                console.log(studentArray)
                const temp = new Map(studentArray)
                const studentObj = Object.fromEntries(temp)

                data && data.push(studentObj)

                //Returns the heading
                return student.user.firstName + " " + student.user.lastName
            })



        }

        if (this.props.editCourse && this.props.editCourse.assignments) {

            //Sort by assignment ID
            // ** Creates the column headers as well as colSchema //
            // ***************************** //
            colHeaders = this.props.editCourse.assignments.map(assignment => {
                const colObj = {data: assignment.id}
                columnSchema.push(colObj)
                return assignment.name + " (" + assignment.totalPoints + ")"
            })

            // ** Creates the row headers //

        }

        const settings = {
            licenseKey: 'non-commercial-and-evaluation',
            data: data,
            colHeaders: colHeaders,
            rowHeaders: rowHeaders,
            rowHeaderWidth: 150,
            columns: columnSchema,
            afterChange: (changes, source) => this.handleChange(change, source),
            //row: rowStudentLabel,
            // width: "600", 
            height: "300",
            headerTooltips: {
                rows: true,
                columns: true,
                onlyTrimmed: false
            }
        }

        return (
            <div id="hot-app" className="table table-hover table-sm">
                <HotTable id="example1" className="my-3" settings={settings} />
            </div>
        );
    }
}

const mapStateToProps = state => ({
    editCourse: state.temp.editCourse,
    data: state.temp.data,
})

export default connect(mapStateToProps, { gridHandleChange })(Table)
