import { GET_USER, HOLD_EDIT_USER, UPDATE_USER } from '../action/types'

const initialState = {
    user: {}
}

export default function(state = initialState, action) {
    switch (action.type) {
        case UPDATE_USER:
            let tempData = {}
            tempData.editUser = {}
            tempData.editUser.user = {}
            tempData.editUser = state.temp && state.temp.editUser
            tempData.editUser.user = action.payload
            return {
                ...state,
                temp: tempData
            };
        case GET_USER:
            return {
                ...state,
                user: action.payload
            }
        case 'REMOVE':
            return state;
        default:
            return state;
    }
}
