import React, { Component } from 'react';
import LoginPage from '../pages/login/LoginPage'
import StudentPage from './../pages/student/StudentPage';
import AdminPage from '../pages/admin/AdminPage';
import {
    BrowserRouter as Router,
    NavLink,
    Switch,
    Route,
    Link,
    Redirect
  } from "react-router-dom";
import ProtectedRoute from './../components/ProtectedRoute';



class Layout extends Component {

    signOut() {
        sessionStorage.clear()
        return <Redirect to="/"/>
    }

    render() {
        
        sessionStorage.clear('user')
        //sessionStorage.setItem('user', true)
        //localStorage.setItem("userType", "ADMIN");

        return (
            <Router>
                <div>
                    <Switch>
                        <Route path={["/", "login-student", "login-parent", "login-staff", "login", "home"]} exact component={LoginPage} />
                        
                        <ProtectedRoute exact={true} path="/admin" component={AdminPage} />
                        <ProtectedRoute exact={true} path="/student" component={StudentPage} />
                        <ProtectedRoute exact={true} path="/teacher" component={AdminPage} />
                        <ProtectedRoute exact={true} path="/parent" component={AdminPage} />

                        <Route path="/signOut" component={this.signOut} />
                    </Switch>
                    
                </div>
            </Router>
        );

        

    }
}

export default Layout;
