import { axios } from 'axios';
import { HOLD_EDIT_USER, UPDATE_STUDENT, UPDATE_USER, HOLD_EDIT_COURSE, HOLD_ADD_STUDENT } from '../action/types';
import { HOLD_EDIT_USER_TYPE } from './../action/types';
//Reducer is a switch statement with actions and defines those actions

const initialState = {}

//Is it better to return the list from the API call and set state to that, or update state on my own
const tempReducer = (state = initialState, action) => {
    switch (action.type) {

        case HOLD_ADD_STUDENT:
            let temp = []
            if(state.studentList) {
                temp = state.studentList
                temp.push(action.payload)
            } else {
                temp.push(action.payload)
            }
            
            return {
                ...state,
                studentList: temp
            }
        case UPDATE_USER: 
            
            let newTemp = {
                ...state.editUser
            }
            newTemp.user = action.payload
            return {
                ...state,
                editUser: newTemp
            }
        case UPDATE_STUDENT:
            return {
                ...state,
                editUser: action.payload
            }
        case HOLD_EDIT_USER:
            return {
                ...state,
                editUser: action.payload
            };
        case HOLD_EDIT_USER_TYPE:
            return {
                ...state,
                editUserType: action.payload
            }
        case HOLD_EDIT_COURSE:
            return {
                ...state,
                editCourse: action.payload
              }
        default:
            return state;
    }
} 

export default tempReducer;
